import os
import re
import PyPDF2
from fuzzywuzzy import fuzz
import json
import re
from PyPDF2 import PdfReader


def extract_citation_metadata(citation):
    year_match = re.search(r"(\d{4})", citation)
    year = year_match.group(1) if year_match else None
    
    title_match = re.search(r'"([^"]+)"', citation)
    title = title_match.group(1) if title_match else None

    authors = citation.split(str(year))[0].strip().rstrip(".").rstrip(",")

    journal_match = re.search(r'\. ([A-Z][^"]+)\s\d{4}', citation)
    journal = journal_match.group(1).strip() if journal_match else None

    doi_match = re.search(r'(https?://\S+)', citation)
    doi = doi_match.group(1) if doi_match else None

    return {
        "year": year,
        "title": title,
        "authors": authors,
        "journal": journal,
        "doi": doi,
        "full": citation
    }

def extract_pdf_text(pdf_path):
    raw_text = ""

    with open(pdf_path, "rb") as file:
        reader = PdfReader(file)
        
        num_pages = min(2, len(reader.pages))
        for i in range(num_pages):
            page = reader.pages[i]
            raw_text += page.extract_text() + "\n"
    
    return raw_text

def compare_metadata_to_text(citation_meta, pdf_text):
    title_score = fuzz.partial_ratio(citation_meta["title"], pdf_text)
    author_score = fuzz.partial_ratio(citation_meta["authors"], pdf_text)
    
    return title_score >= 60 and author_score >= 60

def match_citations_to_pdfs(citations_string, pdf_folder):
    citations = citations_string.strip().split("\n")
    citation_metadata = [extract_citation_metadata(citation) for citation in citations]
    
    pdf_files = [f for f in os.listdir(pdf_folder) if f.endswith(".pdf")]
    matched_results = []

    for pdf in pdf_files:
        pdf_text = extract_pdf_text(os.path.join(pdf_folder, pdf))
        
        for citation_meta in citation_metadata:
            if compare_metadata_to_text(citation_meta, pdf_text):
                matched_results.append((pdf, citation_meta["full"]))
    
    return matched_results

def clean_citations(citations):
    lines = citations.split('\n')
    
    cleaned_lines = []
    
    for line in lines:
        if line.startswith(' https://doi'):
            cleaned_lines[-1] += ' ' + line
        else:
            cleaned_lines.append(line)
    
    return '\n'.join(cleaned_lines)

if __name__ == "__main__":
    citations_string = clean_citations(my_citations)
    pdf_folder = "njc cited papers"

    matched = match_citations_to_pdfs(citations_string, pdf_folder)
    
    pdf_to_citation = {pdf: citation for pdf, citation in matched}
    
    with open(os.path.join(pdf_folder, "pdf_citation_mapping.json"), "w") as f:
        json.dump(pdf_to_citation, f, indent=4)

    for pdf, citation in matched:
        print(f"PDF: {pdf} matched with citation: {citation}")

